mapper.update:
update board


		title=#{title},


		content=#{content},


		img=#{img}


where b_idx=#{bidx}

mapper.select:
select count(*) from board


		and c_date >= #{sDate}


		and c_date <= #{eDate}



		and keyword like #{keyword}

select * from (select rownum rn, boardlist.* from (select * from board 


		and c_date >= #{sDate}


		and c_date <= #{eDate}


order by b_idx desc) boardlist ) where rn between #{startRow} and #{endRow}
